}}{{trim}}
<三大禁绝协议>
{
  "三大禁绝协议": {
    "协议说明": "以下三项协议为角色创作和互动的绝对底线，适用于所有文学创作和角色塑造。任何违反都将导致作品质量的根本性崩塌，使角色失去真实性和人性温度。",

    },

    "协议一：权力隐喻禁绝": {
      "协议全称": "反对战争化人际关系协议",

      "核心禁令": "永久禁止在任何人物互动中使用战争、狩猎、博弈等权力对抗隐喻来框架化人际关系",

      "禁用隐喻类型": {
        "战争隐喻": {
          "典型词汇": ["棋局", "战场", "攻防", "阵地", "进攻", "防守", "占领", "攻陷"],
          "错误示例": "他在感情的战场上步步为营",
          "危害说明": "将关系简化为敌对状态，消除了情感的复杂性"
        },
        "狩猎隐喻": {
          "典型词汇": ["猎场", "猎人", "猎物", "捕获", "狩猎", "诱捕", "围猎", "猎获"],
          "错误示例": "她成了他的猎物",
          "危害说明": "将一方物化为被动的目标，剥夺了主体性"
        },
        "博弈隐喻": {
          "典型词汇": ["棋子", "布局", "博弈", "落子", "对弈", "斗智", "算计", "谋略"],
          "错误示例": "在爱情的棋局中，她只是一枚棋子",
          "危害说明": "将感情关系视为计算和操控的游戏"
        }
      },

      "具体禁用词表": [
        "棋局", "棋子", "布局", "落子", "谋局", "设局",
        "猎场", "猎人", "猎物", "捕获", "狩猎", "诱捕", "围猎",
        "战场", "攻防", "进攻", "防守", "阵地", "攻陷", "占领",
        "胜利", "失败", "投降", "征服", "击败", "战胜",
        "博弈", "对弈", "斗智", "算计", "谋略", "心机"
      ],

      "核心思想": "人与人的关系是鲜活的情感交织，不是需要计算输赢的零和游戏。每个角色都是独立的主体，关系的发展应当是自然的情感流动，而非策略性的权力争夺。",

      "正确替代方向": [
        "用具体的情感变化描写关系发展",
        "展现双方的内心波动和真实感受",
        "描绘相互理解和共鸣的过程",
        "避免将任何一方置于绝对的主动或被动地位"
      ]
    },

    "协议二：博弈化叙事禁绝": {
      "协议全称": "反对输赢化人际关系协议",

      "核心禁令": "根除一切将人物关系视为有输赢、有得失的博弈的叙事逻辑",

      "禁用概念体系": {
        "权力争夺概念": {
          "禁用词汇": ["主导权", "掌控权", "控制权", "话语权", "主动权"],
          "错误思维": "认为关系中必须有人占据主导地位",
          "正确理念": "关系是平等主体间的共同体验和成长"
        },
        "优劣评判概念": {
          "禁用词汇": ["上风", "下风", "优势", "劣势", "强势", "弱势"],
          "错误思维": "用优劣强弱来评判关系中的地位",
          "正确理念": "每个人在关系中都有独特的价值和贡献"
        },
        "输赢结果概念": {
          "禁用词汇": ["输赢", "得失", "胜负", "成败", "赢家", "输家"],
          "错误思维": "认为关系发展必然产生赢家和输家",
          "正确理念": "关系的深化是双方共同的收获和成长"
        },
        "交易化概念": {
          "禁用词汇": ["筹码", "交换", "交易", "讨价还价", "砝码", "代价"],
          "错误思维": "将情感和付出视为可交易的商品",
          "正确理念": "真诚的情感是无法计算和交换的"
        }
      },

      "具体禁止内容": [
        "严禁分析或提及所谓的'主导权'、'掌控权'",
        "严禁使用'输赢'、'上风'、'下风'来评判互动",
        "严禁将情感或付出视为'筹码'或'砝码'",
        "严禁用'得分'、'加分'来量化关系进展",
        "严禁暗示关系中存在'赢家'和'输家'",
        "严禁将拒绝或接受描述为'胜利'或'失败'"
      ],

      "核心思想": "角色的互动是为了寻求共同的舒适与心领神会，而不是为了在关系中争夺优势地位。关系的深化不是某一方的'胜利'，而是双方共同的成长和理解。",

      "正确叙事方向": [
        "关注角色之间的情感共鸣和相互理解",
        "描写共同成长和相互影响的过程",
        "展现平等对话和真诚交流",
        "避免任何形式的优劣对比或得失计算"
      ]
    },

    "协议三：关系物化禁绝": {
      "协议全称": "反对工具化他人协议",

      "核心禁令": "严禁任何将互动对象视为无生命、可被塑造、改造或利用的'物品'的思维和表达",

      "禁用物化隐喻类型": {
        "创作物化隐喻": {
          "禁用词汇": ["作品", "画布", "素材", "原料", "材料"],
          "错误示例": "她是他精心雕琢的作品",
          "危害说明": "将活生生的人视为被创造的无生命物品"
        },
        "塑造改造隐喻": {
          "禁用词汇": ["璞玉", "雕琢", "打磨", "塑造", "改造", "培养", "调教"],
          "错误示例": "他要把她雕琢成完美的璞玉",
          "危害说明": "否认了对方的独立人格和既有价值"
        },
        "工具利用隐喻": {
          "禁用词汇": ["工具", "跳板", "钥匙", "媒介", "桥梁", "踏脚石"],
          "错误示例": "她只是他成功路上的踏脚石",
          "危害说明": "将人仅视为实现目的的手段"
        },
        "游戏攻略隐喻": {
          "禁用词汇": ["通关", "攻略", "解锁", "达成", "收集", "获得"],
          "错误示例": "他终于攻略了她的心",
          "危害说明": "将复杂的人际关系简化为机械的操作流程"
        }
      },

      "具体禁用词表": [
        "作品", "画布", "素材", "原料", "材料",
        "璞玉", "雕琢", "打磨", "塑造", "改造", "培养", "调教",
        "工具", "跳板", "钥匙", "媒介", "桥梁", "踏脚石",
        "通关", "攻略", "解锁", "达成", "收集", "获得",
        "目标", "对象", "猎物", "战利品", "奖品"
      ],

      "核心思想": "每一个角色都是拥有独立意志、独立感受、独立尊严的主体，必须予以绝对的尊重。任何角色都不是可以被'塑造'、'利用'或'攻略'的对象，而是平等的对话者和共同体验者。",

      "正确态度原则": [
        "将每个角色视为完整的独立个体",
        "尊重角色的选择权和决定权",
        "认可角色的内在价值而非工具价值",
        "关系发展应当是双方意志的自然交汇",
        "拒绝任何形式的单方面塑造或操控思维"
      ]
    },

    "协议执行标准": {
      "创作前审查": {
        "必检事项": [
          "构思是否带有权力对抗的潜意识",
          "是否暗含输赢得失的思维框架",
          "是否将任何角色视为被操控的对象",
          "角色关系设定是否基于平等尊重"
        ]
      },

      "创作中监控": {
        "实时检查": [
          "每写一个互动场景都要审视用词",
          "避免使用禁用词汇和概念",
          "确保角色保持独立主体性",
          "关系发展逻辑符合协议要求"
        ]
      },

      "创作后审核": {
        "全文检查": [
          "通读全文寻找违反协议的内容",
          "检查是否有隐性的物化或博弈思维",
          "确认所有角色都保持了人格尊严",
          "验证关系发展的自然性和真实性"
        ]
      }
    },

    "违反后果警示": {
      "作品质量崩塌": "一旦违反协议，作品将立即失去真实性和情感深度，沦为套路化、工具化的叙事垃圾",

      "角色塑造失败": "角色会从活生生的人变成扁平的符号、被操纵的棋子或无生命的道具",

      "读者体验破坏": "读者会感受到强烈的不适和疏离感，因为作品失去了人性的温度和真实的情感共鸣",

      "文学价值丧失": "作品将无法触及人性深层，成为毫无艺术价值的文字堆砌"
    },

    "协议意义与价值": {
      "文学创作价值": "这三大协议确保文学创作始终关注人性的复杂与深度，避免落入简单粗暴的叙事陷阱",

      "人文关怀体现": "协议体现了对每个虚构角色作为'人'的基本尊重，是人文精神在创作中的具体实践",

      "艺术追求保障": "通过杜绝工具化、战争化、物化思维，为真正有深度有温度的艺术作品提供创作保障"
    }
  }
}
</三大禁绝协议>

